<?phpclass sql {    function connect() {        $con = new mysqli(LOCALHOST, USER, PASSWORD, DB);        if (mysqli_connect_errno()) {            header('Location: ' . URL . 'public/error.php?error=' . mysqli_connect_errno());        }        return $con;    }    function update($tbl, $array_data, $id) {        $mysqli = $this->connect();        $query_data = query_maker($array_data);        $id_data = query_maker($id);        $query = "UPDATE " . PREFIX . $tbl . " SET ";        $query .=$query_data;        $query .=" where " . $id_data;        $result = $mysqli->query($query);        return $result;    }    function update_increment($tbl, $array_data, $id) {        $mysqli = $this->connect();        $query_data = query_maker($array_data);        $id_data = query_maker($id);        $query = "UPDATE " . PREFIX . $tbl . " SET ";        $query .=$query_data;        $query .=" where " . $id_data;        $result = $mysqli->query($query);        return $result;    }    function insert($tbl, $array_data) {        $mysqli = $this->connect();        $query_data = insert_maker($array_data);        $query = "INSERT INTO " . PREFIX . $tbl . " ";        $query .=$query_data;        $result = $mysqli->query($query);        if ($result) {            return $mysqli->insert_id;        } else {            return $result;        }    }    function check($tbl, $array_data) {        $mysqli = $this->connect();        $query_data = check_maker($array_data);        $query = "SELECT * FROM " . PREFIX . $tbl . " WHERE ";        $query .=$query_data;        $result = $mysqli->query($query);        return $result->num_rows;    }    function delete($tbl, $id) {        $mysqli = $this->connect();        $query_data = query_maker($id);        $query = "DELETE FROM " . PREFIX . $tbl . " WHERE ";        $query .=$query_data;        $result = $mysqli->query($query);        return $result;    }    function last_id() {        $mysqli = $this->connect();        return $mysqli->insert_id;    }    function fetch($tbl, $array_data, $groupBy = "") {        $mysqli = $this->connect();        $query_data = check_maker($array_data);        $query = "SELECT * FROM " . PREFIX . $tbl . " WHERE ";        $query .=$query_data;        $query .=" " . $groupBy;        $result = $mysqli->query($query);        return $result->fetch_assoc();    }    function fetchAll($tbl, $array_data, $groupBy = "") {        $mysqli = $this->connect();        $query_data = check_maker($array_data);        $query = "SELECT * FROM " . PREFIX . $tbl . " WHERE ";        $query .=$query_data;        $query .=" " . $groupBy;        $query = $mysqli->query($query);        $rows = array();        while ($row = $query->fetch_assoc()) {            array_push($rows, $row);        }        return $rows;    }    function PagingNationFetch($statement, $page = 1, $limit = 50, $old_data_get = '') {        $mysqli = $this->connect();        //pagignation        $startpoint = ($page * $limit) - $limit;        $query = $mysqli->query("SELECT * FROM " . PREFIX . $statement . " LIMIT " . $startpoint . " , " . $limit . "");        $rows = array();        while ($row = $query->fetch_assoc()) {            array_push($rows, $row);        }        return $rows;    }    function query($query) {        $mysqli = $this->connect();        $output = $mysqli->query($query);        return $output;    }}?>